select.execute:

// MD5 hashing from GWT
y.include("http://www.webtoolkit.info/djs/webtoolkit.md5.js");

// create signature
var hm = dev_key + secret + (Math.round(new Date().getTime() / 1000).toString());
y.log(hm);

//var sig = y.crypto.encodeMd5(hm);
//y.log(sig);

//var sig2 = Crypto.be32sToHex( Crypto.strToBe32s(hm) );
//y.log(sig2);

var sig = MD5(hm);

// make request
response.object = request.query("dev_key",dev_key).query("sig",sig).get().response;
